{
  "name": "Test Login Berhasil dengan Kredensial Valid",
  "description": "Memvalidasi bahwa user dapat login dengan username dan password yang benar",
  "type": "positive",
  "priority": "high",
  "steps": [
    {
      "step": "Buka halaman login aplikasi",
      "expectedResult": "Halaman login ditampilkan dengan form username dan password"
    },
    {
      "step": "Masukkan username yang valid: testuser@example.com",
      "expectedResult": "Username berhasil diinput di field username"
    },
    {
      "step": "Masukkan password yang valid: Password123!",
      "expectedResult": "Password berhasil diinput dan tersembunyi (masked)"
    },
    {
      "step": "Klik tombol 'Login'",
      "expectedResult": "Proses autentikasi dimulai"
    }
  ],
  "expectedResult": "User berhasil login dan diarahkan ke halaman dashboard. Session token dibuat dan disimpan.",
  "tags": ["login", "authentication", "positive", "security"]
}

{
  "name": "Test Login Gagal dengan Password Salah",
  "description": "Memvalidasi bahwa sistem menolak login dengan password yang salah",
  "type": "negative",
  "priority": "high",
  "steps": [
    {
      "step": "Buka halaman login aplikasi",
      "expectedResult": "Halaman login ditampilkan dengan form username dan password"
    },
    {
      "step": "Masukkan username yang valid: testuser@example.com",
      "expectedResult": "Username berhasil diinput di field username"
    },
    {
      "step": "Masukkan password yang salah: WrongPassword123",
      "expectedResult": "Password berhasil diinput"
    },
    {
      "step": "Klik tombol 'Login'",
      "expectedResult": "Proses autentikasi dimulai"
    }
  ],
  "expectedResult": "Login ditolak dengan pesan error 'Username atau password salah'. User tetap di halaman login.",
  "tags": ["login", "authentication", "negative", "security", "validation"]
}

{
  "name": "Test Upload File PDF dengan Validasi Ukuran",
  "description": "Memvalidasi fitur upload file PDF dengan batasan ukuran maksimal 5MB",
  "type": "functional",
  "priority": "medium",
  "steps": [
    {
      "step": "Akses halaman upload dokumen",
      "expectedResult": "Halaman upload ditampilkan dengan tombol 'Pilih File'"
    },
    {
      "step": "Klik tombol 'Pilih File'",
      "expectedResult": "Dialog file picker terbuka"
    },
    {
      "step": "Pilih file PDF dengan ukuran 4.8MB",
      "expectedResult": "File terpilih dan nama file ditampilkan"
    },
    {
      "step": "Klik tombol 'Upload'",
      "expectedResult": "Progress bar upload ditampilkan"
    }
  ],
  "expectedResult": "File berhasil diupload, progress bar mencapai 100%, dan muncul notifikasi 'File berhasil diupload'",
  "tags": ["upload", "pdf", "validation", "file-size", "functional"]
}

{
  "name": "Test Logout User",
  "description": "Memvalidasi proses logout user yang sudah login",
  "type": "positive",
  "priority": "medium",
  "steps": [
    {
      "step": "User sudah dalam keadaan login",
      "expectedResult": "Dashboard user ditampilkan"
    },
    {
      "step": "Klik tombol 'Logout' di menu user",
      "expectedResult": "Konfirmasi logout ditampilkan"
    },
    {
      "step": "Konfirmasi logout",
      "expectedResult": "Proses logout dimulai"
    }
  ],
  "expectedResult": "User berhasil logout, session dihapus, dan diarahkan ke halaman login",
  "tags": ["logout", "authentication", "session"],
  "referenceTo": "{{login_test_case_id}}",
  "referenceType": "semantic_search"
}

{
  "name": "Test Login dengan Remember Me",
  "description": "Memvalidasi fitur remember me saat login",
  "type": "positive",
  "priority": "medium",
  "steps": [
    {
      "step": "Buka halaman login aplikasi",
      "expectedResult": "Halaman login ditampilkan dengan form dan checkbox 'Remember Me'"
    },
    {
      "step": "Masukkan username yang valid",
      "expectedResult": "Username berhasil diinput"
    },
    {
      "step": "Masukkan password yang valid",
      "expectedResult": "Password berhasil diinput"
    },
    {
      "step": "Centang checkbox 'Remember Me'",
      "expectedResult": "Checkbox tercentang"
    },
    {
      "step": "Klik tombol 'Login'",
      "expectedResult": "Proses login dimulai"
    }
  ],
  "expectedResult": "User berhasil login dan session cookie disimpan dengan durasi extended (30 hari)",
  "tags": ["login", "authentication", "remember-me", "session"]
}
